public class TransactionProcessor {
    public static void main(String[] args) {
        String transactionDetails = "REF#XYZ123-AMOUNT:450.75-TAX:10-ID:887";

        int amountStart = transactionDetails.indexOf("AMOUNT:") + "AMOUNT:".length();
        int amountEnd = transactionDetails.indexOf("-TAX:");
        String amountString = transactionDetails.substring(amountStart, amountEnd);
        double baseAmount = Double.parseDouble(amountString);

        int taxStart = transactionDetails.indexOf("TAX:") + "TAX:".length();
        int taxEnd = transactionDetails.indexOf("-ID:");
        String taxPercentString = transactionDetails.substring(taxStart, taxEnd);
        int taxPercentage = Integer.parseInt(taxPercentString);

        double finalAmount = baseAmount * (1 + (double)taxPercentage / 100);

        System.out.printf("Son Tutar: %.2f TL", finalAmount);
    }
}

