def decorator(func):
    def wrapper(*args):
        print("Before")
        result = func(*args)
        print("After")
        return result
    return wrapper

@decorator
def say(msg):
    print(msg)
    return len(msg)

x = say("Hi")

def memoize(func):
    cache = {}
    def wrapper(n):
        if n not in cache:
            cache[n] = func(n)
        return cache[n]
    return wrapper

calls = 0
@memoize
def fib(n):
    global calls
    calls += 1
    if n < 2: return n
    return fib(n-1) + fib(n-2)

fib(5)
print(calls)

def curry(f):
    def curried(a):
        def inner(b):
            return f(a, b)
        return inner
    return curried

