def eval_rpn(tokens):
    stack = []
    for t in tokens:
        if t in "+-*/":
            b, a = stack.pop(), stack.pop()
            if t=="+": stack.append(a+b)
            elif t=="-": stack.append(a-b)
            elif t=="*": stack.append(a*b)
        else:
            stack.append(int(t))
    return stack[0]

